const hamburger=document.querySelector(".hamburger"),menu=document.querySelector(".menu"),overlay=document.querySelector(".overlay");hamburger.addEventListener("click",()=>{document.body.classList.toggle("show-menu")}),overlay.addEventListener("click",()=>{document.body.classList.remove("show-menu")});const menuItems=document.querySelectorAll(".menu-item");menuItems.forEach(e=>{e.addEventListener("click",()=>{const t=e.nextElementSibling;t&&t.classList.contains("sub-menu")&&t.classList.toggle("show-sub-menu")})});function openSearch(){document.getElementById("dialogSearch").style.display="block",document.getElementById("blurBackground").style.display="block"}function closeSearch(){document.getElementById("dialogSearch").style.display="none",document.getElementById("blurBackground").style.display="none"}document.addEventListener("DOMContentLoaded",function(){const t=document.querySelectorAll("#local-search-input, #local-search-input2, #local-search-input3"),n=document.querySelectorAll("#local-search-result, #local-search-result2, #local-search-result3");let e=[];fetch("/index.json").then(e=>e.json()).then(o=>{e=o.map(e=>({title:e.title||"Untitled",url:e.url||e.permalink})),t.forEach((t,o)=>{const i=n[o];t.addEventListener("input",function(){const t=this.value.trim().toLowerCase();if(i.innerHTML="",i.style.display=t?"block":"none",!t)return;const n=e.filter(e=>{const n=e.title.toLowerCase();return n.includes(t)});if(n.length===0){i.innerHTML='<p class="no-results">no-results</p>';return}const o=n.map(e=>`
                      <li class="search-result-item">
                          <a href="${e.url}">${s(t,e.title)}</a>
                      </li>
                  `).join("");i.innerHTML=`<ul class="search-result-list">${o}</ul>`})})}).catch(e=>{console.error("error:",e)});function s(e,t){const n=new RegExp(`(${e})`,"gi");return t.replace(n,"<mark>$1</mark>")}})